#!/usr/bin/env node

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

console.log('ğŸ” Verificando performance e problemas do site SVlentes...\n');

// FunÃ§Ã£o para executar comandos e capturar saÃ­da
function runCommand(command, description) {
    console.log(`ğŸ“‹ ${description}...`);
    try {
        const output = execSync(command, { encoding: 'utf8', stdio: 'pipe' });
        console.log('âœ… Sucesso\n');
        return output;
    } catch (error) {
        console.log(`âŒ Erro: ${error.message}\n`);
        return null;
    }
}

// FunÃ§Ã£o para verificar arquivos
function checkFile(filePath, description) {
    console.log(`ğŸ“ Verificando ${description}...`);
    if (fs.existsSync(filePath)) {
        console.log('âœ… Arquivo encontrado\n');
        return true;
    } else {
        console.log(`âŒ Arquivo nÃ£o encontrado: ${filePath}\n`);
        return false;
    }
}

// VerificaÃ§Ãµes de arquivos crÃ­ticos
console.log('=== VERIFICAÃ‡ÃƒO DE ARQUIVOS CRÃTICOS ===\n');

const criticalFiles = [
    { path: 'src/app/layout.tsx', desc: 'Layout principal' },
    { path: 'src/app/page.tsx', desc: 'PÃ¡gina inicial' },
    { path: 'src/components/layout/Header.tsx', desc: 'Header' },
    { path: 'src/components/sections/FAQ.tsx', desc: 'FAQ' },
    { path: 'src/components/forms/EconomyCalculator.tsx', desc: 'Calculadora' },
    { path: 'src/app/termos-uso/page.tsx', desc: 'Termos de uso' },
    { path: 'next.config.js', desc: 'ConfiguraÃ§Ã£o Next.js' },
];

let allFilesExist = true;
criticalFiles.forEach(file => {
    if (!checkFile(file.path, file.desc)) {
        allFilesExist = false;
    }
});

// VerificaÃ§Ã£o de build
console.log('=== VERIFICAÃ‡ÃƒO DE BUILD ===\n');
const buildOutput = runCommand('npm run build', 'Build do projeto');

// VerificaÃ§Ã£o de lint
console.log('=== VERIFICAÃ‡ÃƒO DE LINT ===\n');
runCommand('npm run lint', 'VerificaÃ§Ã£o de cÃ³digo');

// VerificaÃ§Ã£o de tipos TypeScript
console.log('=== VERIFICAÃ‡ÃƒO DE TIPOS ===\n');
runCommand('npx tsc --noEmit', 'VerificaÃ§Ã£o de tipos TypeScript');

// AnÃ¡lise de bundle
console.log('=== ANÃLISE DE BUNDLE ===\n');
if (fs.existsSync('.next')) {
    console.log('ğŸ“Š Analisando tamanho do bundle...');

    // Verificar se existe o arquivo de build stats
    const buildStatsPath = '.next/static/chunks';
    if (fs.existsSync(buildStatsPath)) {
        const chunks = fs.readdirSync(buildStatsPath);
        console.log(`ğŸ“¦ Total de chunks: ${chunks.length}`);

        // Verificar tamanhos dos chunks principais
        const mainChunks = chunks.filter(chunk =>
            chunk.includes('main') ||
            chunk.includes('pages') ||
            chunk.includes('app')
        );

        console.log('ğŸ“‹ Chunks principais:');
        mainChunks.forEach(chunk => {
            const chunkPath = path.join(buildStatsPath, chunk);
            const stats = fs.statSync(chunkPath);
            const sizeKB = (stats.size / 1024).toFixed(2);
            console.log(`   ${chunk}: ${sizeKB} KB`);
        });
    }
    console.log('âœ… AnÃ¡lise de bundle concluÃ­da\n');
} else {
    console.log('âŒ Build nÃ£o encontrado. Execute npm run build primeiro.\n');
}

// VerificaÃ§Ã£o de variÃ¡veis de ambiente
console.log('=== VERIFICAÃ‡ÃƒO DE VARIÃVEIS DE AMBIENTE ===\n');
checkFile('.env.example', 'Arquivo de exemplo de variÃ¡veis');
checkFile('.env.local', 'Arquivo de variÃ¡veis locais');

// VerificaÃ§Ã£o de dependÃªncias
console.log('=== VERIFICAÃ‡ÃƒO DE DEPENDÃŠNCIAS ===\n');
runCommand('npm audit --audit-level=moderate', 'Auditoria de seguranÃ§a');

// VerificaÃ§Ã£o de performance
console.log('=== VERIFICAÃ‡ÃƒO DE PERFORMANCE ===\n');

const performanceChecks = [
    {
        name: 'Imagens otimizadas',
        check: () => {
            const publicDir = 'public';
            if (!fs.existsSync(publicDir)) return false;

            const images = fs.readdirSync(publicDir).filter(file =>
                file.match(/\.(jpg|jpeg|png|gif|webp|avif)$/i)
            );

            console.log(`ğŸ“¸ Encontradas ${images.length} imagens em /public`);
            return images.length > 0;
        }
    },
    {
        name: 'ConfiguraÃ§Ã£o de cache',
        check: () => {
            const nextConfig = fs.readFileSync('next.config.js', 'utf8');
            return nextConfig.includes('headers') && nextConfig.includes('Cache-Control');
        }
    },
    {
        name: 'CompressÃ£o habilitada',
        check: () => {
            const nextConfig = fs.readFileSync('next.config.js', 'utf8');
            return nextConfig.includes('compress: true');
        }
    }
];

performanceChecks.forEach(check => {
    console.log(`ğŸ” Verificando ${check.name}...`);
    if (check.check()) {
        console.log('âœ… OK\n');
    } else {
        console.log('âš ï¸  Pode ser melhorado\n');
    }
});

// Resumo final
console.log('=== RESUMO FINAL ===\n');

if (allFilesExist) {
    console.log('âœ… Todos os arquivos crÃ­ticos estÃ£o presentes');
} else {
    console.log('âŒ Alguns arquivos crÃ­ticos estÃ£o faltando');
}

if (buildOutput) {
    console.log('âœ… Build executado com sucesso');
} else {
    console.log('âŒ Problemas no build detectados');
}

console.log('\nğŸ¯ PRÃ“XIMOS PASSOS RECOMENDADOS:');
