### 2. Server Actions para Assinaturas
```typescript
// lib/actions/subscription.ts
'use server'

import { auth } from '@/lib/auth'
import { db } from '@/lib/db'
import { stripe } from '@/lib/payments/stripe'
import { revalidatePath } from 'next/cache'
import { redirect } from 'next/navigation'
import { z } from 'zod'

const createSubscriptionSchema = z.object({
  planId: z.string(),
  paymentMethodId: z.string(),
  billingAddress: z.object({
    street: z.string(),
    city: z.string(),
    state: z.string(),
    zipCode: z.string()
  })
})

export async function createSubscription(formData: FormData) {
  const session = await auth()
  if (!session?.user) {
    redirect('/login')
  }

  const validatedFields = createSubscriptionSchema.safeParse({
    planId: formData.get('planId'),
    paymentMethodId: formData.get('paymentMethodId'),
    billingAddress: {
      street: formData.get('street'),
      city: formData.get('city'),
      state: formData.get('state'),
      zipCode: formData.get('zipCode')
    }
  })

  if (!validatedFields.success) {
    return { error: 'Dados invÃ¡lidos' }
  }

  try {
    // Buscar plano
    const plan = await db.plan.findUnique({
      where: { id: validatedFields.data.planId }
    })

    if (!plan) {
      return { error: 'Plano nÃ£o encontrado' }
    }

    // Criar assinatura no Stripe
    const stripeSubscription = await stripe.subscriptions.create({
      customer: session.user.stripeCustomerId,
      items: [{ price: plan.stripePriceId }],
      payment_behavior: 'default_incomplete',
      payment_settings: { save_default_payment_method: 'on_subscription' },
      expand: ['latest_invoice.payment_intent'],
    })

    // Salvar no banco
    const subscription = await db.subscription.create({
      data: {
        userId: session.user.id,
        planId: validatedFields.data.planId,
        stripeSubscriptionId: stripeSubscription.id,
        status: 'active',
        currentPeriodStart: new Date(stripeSubscription.current_period_start * 1000),
        currentPeriodEnd: new Date(stripeSubscription.current_period_end * 1000),
        amount: plan.price,
        billingAddress: validatedFields.data.billingAddress
      }
    })

    revalidatePath('/dashboard/subscription')
    return { success: true, subscriptionId: subscription.id }

  } catch (error) {
    console.error('Erro ao criar assinatura:', error)
    return { error: 'Erro interno do servidor' }
  }
}

export async function pauseSubscription(subscriptionId: string) {
  const session = await auth()
  if (!session?.user) {
    redirect('/login')
  }

  try {
    const subscription = await db.subscription.findFirst({
      where: { 
        id: subscriptionId, 
        userId: session.user.id 
      }
    })

    if (!subscription) {
      return { error: 'Assinatura nÃ£o encontrada' }
    }

    // Pausar no Stripe
    await stripe.subscriptions.update(subscription.stripeSubscriptionId, {
      pause_collection: {
        behavior: 'keep_as_draft'
      }
    })

    // Atualizar no banco
    await db.subscription.update({
      where: { id: subscriptionId },
      data: { status: 'paused' }
    })

    revalidatePath('/dashboard/subscription')
    return { success: true }

  } catch (error) {
    console.error('Erro ao pausar assinatura:', error)
    return { error: 'Erro interno do servidor' }
  }
}

export async function cancelSubscription(subscriptionId: string, reason?: string) {
  const session = await auth()
  if (!session?.user) {
    redirect('/login')
  }

  try {
    const subscription = await db.subscription.findFirst({
      where: { 
        id: subscriptionId, 
        userId: session.user.id 
      }
    })

    if (!subscription) {
      return { error: 'Assinatura nÃ£o encontrada' }
    }

    // Cancelar no Stripe
    await stripe.subscriptions.cancel(subscription.stripeSubscriptionId)

    // Atualizar no banco
    await db.subscription.update({
      where: { id: subscriptionId },
      data: { 
        status: 'canceled',
        canceledAt: new Date(),
        cancellationReason: reason
      }
    })

    revalidatePath('/dashboard/subscription')
    return { success: true }

  } catch (error) {
    console.error('Erro ao cancelar assinatura:', error)
    return { error: 'Erro interno do servidor' }
  }
}
