### 4. Telemedicina com WebRTC
```typescript
// components/medical/video-call.tsx
'use client'

import { useEffect, useRef, useState } from 'react'
import { Button } from '@/components/ui/button'
import { Mic, MicOff, Video, VideoOff, Phone } from 'lucide-react'

interface VideoCallProps {
  roomId: string
  userId: string
  userType: 'doctor' | 'patient'
}

export function VideoCall({ roomId, userId, userType }: VideoCallProps) {
  const localVideoRef = useRef<HTMLVideoElement>(null)
  const remoteVideoRef = useRef<HTMLVideoElement>(null)
  const peerConnectionRef = useRef<RTCPeerConnection | null>(null)
  const socketRef = useRef<WebSocket | null>(null)

  const [isAudioEnabled, setIsAudioEnabled] = useState(true)
  const [isVideoEnabled, setIsVideoEnabled] = useState(true)
  const [isConnected, setIsConnected] = useState(false)
  const [localStream, setLocalStream] = useState<MediaStream | null>(null)

  useEffect(() => {
    initializeCall()
    return () => {
      cleanup()
    }
  }, [])

  const initializeCall = async () => {
    try {
      // Configurar WebSocket
      socketRef.current = new WebSocket(`${process.env.NEXT_PUBLIC_WS_URL}/medical/${roomId}`)

      socketRef.current.onopen = () => {
        socketRef.current?.send(JSON.stringify({
          type: 'join',
          userId,
          userType
        }))
      }

      socketRef.current.onmessage = handleSocketMessage

      // Obter mÃ­dia local
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      })

      setLocalStream(stream)
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream
      }

      // Configurar WebRTC
      setupPeerConnection(stream)

    } catch (error) {
      console.error('Erro ao inicializar chamada:', error)
    }
  }

  const setupPeerConnection = (stream: MediaStream) => {
    const peerConnection = new RTCPeerConnection({
      iceServers: [
        { urls: 'stun:stun.l.google.com:19302' },
        { urls: 'stun:stun1.l.google.com:19302' }
      ]
    })

    peerConnectionRef.current = peerConnection

    // Adicionar stream local
    stream.getTracks().forEach(track => {
      peerConnection.addTrack(track, stream)
    })

    // Lidar com stream remoto
    peerConnection.ontrack = (event) => {
      if (remoteVideoRef.current) {
        remoteVideoRef.current.srcObject = event.streams[0]
      }
      setIsConnected(true)
    }

    // Lidar com ICE candidates
    peerConnection.onicecandidate = (event) => {
      if (event.candidate && socketRef.current) {
        socketRef.current.send(JSON.stringify({
          type: 'ice-candidate',
          candidate: event.candidate
        }))
      }
    }
  }

  const handleSocketMessage = async (event: MessageEvent) => {
    const message = JSON.parse(event.data)
    const peerConnection = peerConnectionRef.current

    if (!peerConnection) return

    switch (message.type) {
      case 'offer':
        await peerConnection.setRemoteDescription(message.offer)
        const answer = await peerConnection.createAnswer()
        await peerConnection.setLocalDescription(answer)

        socketRef.current?.send(JSON.stringify({
          type: 'answer',
          answer
        }))
        break

      case 'answer':
        await peerConnection.setRemoteDescription(message.answer)
        break

      case 'ice-candidate':
        await peerConnection.addIceCandidate(message.candidate)
        break

      case 'user-joined':
        if (userType === 'doctor') {
          // MÃ©dico inicia a chamada
          const offer = await peerConnection.createOffer()
          await peerConnection.setLocalDescription(offer)

          socketRef.current?.send(JSON.stringify({
            type: 'offer',
            offer
          }))
        }
        break
    }
  }

  const toggleAudio = () => {
    if (localStream) {
      const audioTrack = localStream.getAudioTracks()[0]
      if (audioTrack) {
        audioTrack.enabled = !audioTrack.enabled
        setIsAudioEnabled(audioTrack.enabled)
      }
    }
  }

  const toggleVideo = () => {
    if (localStream) {
      const videoTrack = localStream.getVideoTracks()[0]
      if (videoTrack) {
        videoTrack.enabled = !videoTrack.enabled
        setIsVideoEnabled(videoTrack.enabled)
      }
    }
  }

  const endCall = () => {
    cleanup()
    // Redirecionar ou fechar modal
  }

  const cleanup = () => {
    if (localStream) {
      localStream.getTracks().forEach(track => track.stop())
    }

    if (peerConnectionRef.current) {
      peerConnectionRef.current.close()
    }

    if (socketRef.current) {
      socketRef.current.close()
    }
  }

  return (
    <div className="flex flex-col h-screen bg-gray-900">
      <div className="flex-1 relative">
        {/* VÃ­deo remoto */}
        <video
          ref={remoteVideoRef}
          autoPlay
          playsInline
          className="w-full h-full object-cover"
        />

        {/* VÃ­deo local */}
        <div className="absolute bottom-4 right-4 w-48 h-36 bg-gray-800 rounded-lg overflow-hidden">
          <video
            ref={localVideoRef}
            autoPlay
            playsInline
            muted
            className="w-full h-full object-cover"
          />
        </div>

        {/* Status de conexÃ£o */}
        {!isConnected && (
          <div className="absolute top-4 left-4 bg-yellow-500 text-white px-3 py-1 rounded">
            Conectando...
          </div>
        )}
      </div>

      {/* Controles */}
      <div className="flex justify-center items-center p-4 bg-gray-800 space-x-4">
        <Button
          variant={isAudioEnabled ? "default" : "destructive"}
          size="lg"
          onClick={toggleAudio}
        >
          {isAudioEnabled ? <Mic /> : <MicOff />}
        </Button>

        <Button
          variant={isVideoEnabled ? "default" : "destructive"}
          size="lg"
          onClick={toggleVideo}
        >
          {isVideoEnabled ? <Video /> : <VideoOff />}
        </Button>

        <Button
          variant="destructive"
          size="lg"
          onClick={endCall}
        >
          <Phone />
        </Button>
      </div>
    </div>
  )
}
