```typescript
// app/api/cron/billing/route.ts
import { db } from '@/lib/db'
import { stripe } from '@/lib/payments/stripe'
import { NextResponse } from 'next/server'

export async function GET() {
  // Verificar se Ã© uma requisiÃ§Ã£o autorizada do Vercel Cron
  if (process.env.NODE_ENV === 'production') {
    const authHeader = headers().get('authorization')
    if (authHeader !== `Bearer ${process.env.CRON_SECRET}`) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }
  }

  try {
    // Processar cobranÃ§as pendentes
    const pendingSubscriptions = await db.subscription.findMany({
      where: {
        status: 'active',
        nextBillingDate: {
          lte: new Date()
        }
      },
      include: {
        user: true,
        plan: true
      }
    })

    const results = []

    for (const subscription of pendingSubscriptions) {
      try {
        // Criar invoice no Stripe
        const invoice = await stripe.invoices.create({
          customer: subscription.user.stripeCustomerId,
          subscription: subscription.stripeSubscriptionId,
          auto_advance: true
        })

        await stripe.invoices.finalizeInvoice(invoice.id)

        results.push({
          subscriptionId: subscription.id,
          status: 'success',
          invoiceId: invoice.id
        })

      } catch (error) {
        console.error(`Erro ao processar cobranÃ§a ${subscription.id}:`, error)
        results.push({
          subscriptionId: subscription.id,
          status: 'error',
          error: error.message
        })
      }
    }

    return NextResponse.json({
      processed: results.length,
      results
    })

  } catch (error) {
    console.error('Erro no cron de cobranÃ§a:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

