  private determineVariant(
    persona: string, 
    engagement: string, 
    conversionProb: number
  ): string {
    if (conversionProb > 0.7) return 'high-intent'
    if (engagement === 'low') return 'reengagement'
    if (engagement === 'high') return 'engaged'
    return `${persona}-default`
  }

