        font-family: var(--font-poppins), system-ui, sans-serif;
        font-weight: 600;
        line-height: 1.2;
    }

    /* Animations */
    @keyframes fade-in {
        from {
            opacity: 0;
            transform: translateY(20px);
        }

        to {
            opacity: 1;
            transform: translateY(0);
        }
    }

    @keyframes slide-up {
        from {
            opacity: 0;
            transform: translateY(40px);
        }

        to {
            opacity: 1;
            transform: translateY(0);
        }
    }

    @keyframes pulse-slow {

        0%,
        100% {
            opacity: 0.5;
        }

        50% {
            opacity: 0.8;
        }
    }

    .animate-fade-in {
        animation: fade-in 0.8s ease-out;
    }

    .animate-slide-up {
        animation: slide-up 1s ease-out;
    }

    .animate-pulse-slow {
        animation: pulse-slow 6s ease-in-out infinite;
    }
}

@layer components {
    .btn-primary {
        @apply bg-primary-600 hover:bg-primary-700 active:bg-primary-800 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 shadow-md hover:shadow-lg;
    }

    .btn-secondary {
        @apply bg-secondary-500 hover:bg-secondary-600 active:bg-secondary-700 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-secondary-400 focus:ring-offset-2 shadow-md hover:shadow-lg;
    }

    .btn-outline {
        @apply border-2 border-primary-600 text-primary-700 hover:bg-primary-600 hover:text-white active:bg-primary-700 font-semibold py-3 px-6 rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 shadow-sm hover:shadow-md;
    }

    .btn-whatsapp {
        @apply bg-whatsapp-500 hover:bg-whatsapp-600 active:bg-whatsapp-700 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-whatsapp-400 focus:ring-offset-2 shadow-md hover:shadow-lg;
    }

    .btn-success {
        @apply bg-success-600 hover:bg-success-700 active:bg-success-800 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-success-500 focus:ring-offset-2 shadow-md hover:shadow-lg;
    }

    .btn-warning {
        @apply bg-warning-500 hover:bg-warning-600 active:bg-warning-700 text-warning-900 font-semibold py-3 px-6 rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-warning-400 focus:ring-offset-2 shadow-md hover:shadow-lg;
    }

    .container-custom {
        @apply max-w-7xl mx-auto px-4 sm:px-6 lg:px-8;
    }

    .section-padding {
        @apply py-16 lg:py-24;
    }

    .section-header {
        @apply text-center mb-12 lg:mb-16;
    }

    .section-title {
        @apply text-3xl md:text-4xl lg:text-5xl font-bold text-foreground mb-4;
    }

    .section-subtitle {
        @apply text-lg md:text-xl text-muted-foreground max-w-3xl mx-auto;
    }

    .card {
        @apply bg-card rounded-xl shadow-lg border border-border p-6 hover:shadow-xl transition-shadow duration-300;
    }

    .input-field {
        @apply w-full px-4 py-3 border border-input rounded-lg text-foreground placeholder:text-muted-foreground focus:ring-2 focus:ring-ring focus:border-ring transition-all duration-200 disabled:bg-muted disabled:cursor-not-allowed disabled:opacity-50;
    }

    .text-gradient {
        @apply bg-gradient-to-r from-primary-600 to-success-600 bg-clip-text text-transparent;
    }

    .text-gradient-medical {
        @apply bg-gradient-to-r from-primary-600 to-primary-400 bg-clip-text text-transparent;
    }

    .bg-gradient-medical {
        @apply bg-gradient-to-br from-primary-50 to-primary-100;
    }

    .bg-gradient-success {
        @apply bg-gradient-to-br from-success-50 to-success-100;
    }

    /* Custom Range Slider Styles */
    input[type="range"].slider {
        -webkit-appearance: none;
        appearance: none;
    }

    input[type="range"].slider::-webkit-slider-thumb {
        -webkit-appearance: none;
        appearance: none;
        width: 24px;
        height: 24px;
        border-radius: 50%;
        background: #2563eb;
        cursor: pointer;
        box-shadow: 0 2px 8px rgba(37, 99, 235, 0.4);
        transition: all 0.2s ease;
    }

    input[type="range"].slider::-webkit-slider-thumb:hover {
        background: #1d4ed8;
        transform: scale(1.1);
        box-shadow: 0 4px 12px rgba(37, 99, 235, 0.6);
    }

    input[type="range"].slider::-moz-range-thumb {
        width: 24px;
        height: 24px;
        border-radius: 50%;
        background: #2563eb;
        cursor: pointer;
        border: none;
        box-shadow: 0 2px 8px rgba(37, 99, 235, 0.4);
        transition: all 0.2s ease;
    }

    input[type="range"].slider::-moz-range-thumb:hover {
        background: #1d4ed8;
        transform: scale(1.1);
        box-shadow: 0 4px 12px rgba(37, 99, 235, 0.6);
    }

    input[type="range"].slider:focus {
        outline: none;
    }

    input[type="range"].slider:focus::-webkit-slider-thumb {
        ring: 2px;
        ring-color: #2563eb;
        ring-offset: 2px;
    }
}

