import { type ClassValue, clsx } from 'clsx'
import { twMerge } from 'tailwind-merge'

export function cn(...inputs: ClassValue[]) {
    return twMerge(clsx(inputs))
}

// FunÃ§Ã£o para scroll suave para seÃ§Ãµes
export function scrollToSection(sectionId: string) {
    const element = document.getElementById(sectionId)
    if (element) {
        element.scrollIntoView({
            behavior: 'smooth',
            block: 'start',
        })
    }
}

// FunÃ§Ã£o para gerar link do WhatsApp
export function generateWhatsAppLink(
    phone: string,
    message: string,
    userData?: {
        nome?: string
        email?: string
        planInterest?: string
    }
) {
    const cleanPhone = phone.replace(/\D/g, '')

    let finalMessage = message

    if (userData) {
        if (userData.nome) {
            finalMessage += `\n\nNome: ${userData.nome}`
        }
        if (userData.email) {
            finalMessage += `\nEmail: ${userData.email}`
        }
        if (userData.planInterest) {
            finalMessage += `\nInteresse: ${userData.planInterest}`
        }
    }

    const encodedMessage = encodeURIComponent(finalMessage)
    return `https://wa.me/${cleanPhone}?text=${encodedMessage}`
}

// FunÃ§Ã£o para formatar moeda brasileira
export function formatCurrency(value: number): string {
    return new Intl.NumberFormat('pt-BR', {
        style: 'currency',
        currency: 'BRL',
    }).format(value)
}

// FunÃ§Ã£o para calcular desconto percentual
export function calculateDiscount(originalPrice: number, discountedPrice: number): number {
    return Math.round(((originalPrice - discountedPrice) / originalPrice) * 100)
}

// FunÃ§Ã£o para debounce
export function debounce<T extends (...args: any[]) => any>(
    func: T,
    wait: number
): (...args: Parameters<T>) => void {
    let timeout: NodeJS.Timeout
    return (...args: Parameters<T>) => {
        clearTimeout(timeout)
        timeout = setTimeout(() => func(...args), wait)
    }
}

// FunÃ§Ã£o para verificar se estÃ¡ em dispositivo mÃ³vel
export function isMobile(): boolean {
    if (typeof window === 'undefined') return false
    return window.innerWidth < 768
}

// FunÃ§Ã£o para gerar ID Ãºnico
export function generateId(): string {
    return Math.random().toString(36).substr(2, 9)
}

