            .max(100, 'Cidade deve ter no mÃ¡ximo 100 caracteres'),
        state: z.string()
            .length(2, 'Estado deve ter 2 caracteres')
            .regex(/^[A-Z]{2}$/, 'Estado deve ser uma sigla vÃ¡lida (ex: SP)')
    })
})

// Schema para dados de prescriÃ§Ã£o mÃ©dica
export const prescriptionSchema = z.object({
    hasValidPrescription: z.boolean(),
    prescriptionFile: z.any().optional(), // File serÃ¡ validado no frontend
    rightEye: z.object({
        sphere: z.number()
            .min(-20, 'Esfera deve estar entre -20 e +20')
            .max(20, 'Esfera deve estar entre -20 e +20'),
        cylinder: z.number()
            .min(-10, 'Cilindro deve estar entre -10 e +10')
            .max(10, 'Cilindro deve estar entre -10 e +10'),
        axis: z.number()
            .min(0, 'Eixo deve estar entre 0 e 180')
            .max(180, 'Eixo deve estar entre 0 e 180')
    }),
    leftEye: z.object({
        sphere: z.number()
            .min(-20, 'Esfera deve estar entre -20 e +20')
            .max(20, 'Esfera deve estar entre -20 e +20'),
        cylinder: z.number()
            .min(-10, 'Cilindro deve estar entre -10 e +10')
            .max(10, 'Cilindro deve estar entre -10 e +10'),
        axis: z.number()
            .min(0, 'Eixo deve estar entre 0 e 180')
            .max(180, 'Eixo deve estar entre 0 e 180')
    }),
    prescriptionDate: z.string()
        .regex(/^\d{4}-\d{2}-\d{2}$/, 'Data deve estar no formato YYYY-MM-DD'),
    doctorName: z.string()
        .min(5, 'Nome do mÃ©dico deve ter pelo menos 5 caracteres')
        .max(200, 'Nome do mÃ©dico deve ter no mÃ¡ximo 200 caracteres'),
    needsConsultation: z.boolean()
})

// Schema para preferÃªncias do cliente
export const preferencesSchema = z.object({
    lensType: z.enum(['daily', 'weekly', 'monthly'], {
        errorMap: () => ({ message: 'Tipo de lente invÃ¡lido' })
    }),
    deliveryFrequency: z.enum(['monthly', 'quarterly', 'semiannual'], {
        errorMap: () => ({ message: 'FrequÃªncia de entrega invÃ¡lida' })
    }),
    specialNeeds: z.string()
        .max(500, 'Necessidades especiais devem ter no mÃ¡ximo 500 caracteres')
        .optional(),
    addOns: z.array(z.string())
        .max(10, 'MÃ¡ximo de 10 add-ons permitidos')
})

// Schema completo para formulÃ¡rio de assinatura
export const subscriptionFormSchema = z.object({
    leadInfo: leadFormSchema,
    personalInfo: personalInfoSchema,
    prescription: prescriptionSchema,
    preferences: preferencesSchema,
    selectedPlan: z.enum(['basic', 'premium', 'vip'], {
        errorMap: () => ({ message: 'Plano selecionado invÃ¡lido' })
    })
})

// Schema para contato via WhatsApp
export const whatsappContactSchema = z.object({
    type: z.enum(['lead', 'consultation', 'support'], {
        errorMap: () => ({ message: 'Tipo de contato invÃ¡lido' })
    }),
    userData: leadFormSchema.partial(),
    context: z.object({
        page: z.string().min(1, 'PÃ¡gina Ã© obrigatÃ³ria'),
        planInterest: z.string().optional(),
        calculatedEconomy: z.number().optional()
    }),
    prefilledMessage: z.string()
        .min(10, 'Mensagem deve ter pelo menos 10 caracteres')
        .max(1000, 'Mensagem deve ter no mÃ¡ximo 1000 caracteres')
})

// FunÃ§Ã£o para validar CPF
export function validateCPF(cpf: string): boolean {
    // Remove caracteres nÃ£o numÃ©ricos
    const cleanCPF = cpf.replace(/\D/g, '')

    // Verifica se tem 11 dÃ­gitos
    if (cleanCPF.length !== 11) return false

    // Verifica se todos os dÃ­gitos sÃ£o iguais
    if (/^(\d)\1{10}$/.test(cleanCPF)) return false

    // ValidaÃ§Ã£o do primeiro dÃ­gito verificador
    let sum = 0
    for (let i = 0; i < 9; i++) {
        sum += parseInt(cleanCPF[i]) * (10 - i)
    }
    let remainder = sum % 11
    let digit1 = remainder < 2 ? 0 : 11 - remainder

    if (parseInt(cleanCPF[9]) !== digit1) return false

    // ValidaÃ§Ã£o do segundo dÃ­gito verificador
    sum = 0
    for (let i = 0; i < 10; i++) {
        sum += parseInt(cleanCPF[i]) * (11 - i)
    }
    remainder = sum % 11
    let digit2 = remainder < 2 ? 0 : 11 - remainder

    return parseInt(cleanCPF[10]) === digit2
}

// FunÃ§Ã£o para validar CEP
export function validateCEP(cep: string): boolean {
    const cleanCEP = cep.replace(/\D/g, '')
    return /^\d{8}$/.test(cleanCEP)
}

// FunÃ§Ã£o para formatar telefone/WhatsApp
export function formatPhone(phone: string): string {
    const cleanPhone = phone.replace(/\D/g, '')

    if (cleanPhone.length === 11) {
        return cleanPhone.replace(/(\d{2})(\d{5})(\d{4})/, '($1) $2-$3')
    } else if (cleanPhone.length === 10) {
        return cleanPhone.replace(/(\d{2})(\d{4})(\d{4})/, '($1) $2-$3')
    }

    return phone
}

// FunÃ§Ã£o para formatar CPF
export function formatCPF(cpf: string): string {
    const cleanCPF = cpf.replace(/\D/g, '')
    return cleanCPF.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4')
}

// FunÃ§Ã£o para formatar CEP
export function formatCEP(cep: string): string {
    const cleanCEP = cep.replace(/\D/g, '')
    return cleanCEP.replace(/(\d{5})(\d{3})/, '$1-$2')
}

// Tipos derivados dos schemas
export type LeadFormData = z.infer<typeof leadFormSchema>
export type CalculatorData = z.infer<typeof calculatorSchema>
export type PersonalInfoData = z.infer<typeof personalInfoSchema>
export type PrescriptionData = z.infer<typeof prescriptionSchema>
export type PreferencesData = z.infer<typeof preferencesSchema>
export type SubscriptionFormData = z.infer<typeof subscriptionFormSchema>
export type WhatsAppContactData = z.infer<typeof whatsappContactSchema>

