        'Suporte tÃ©cnico',
        'EmergÃªncia com lentes'
    ]
}

// FunÃ§Ã£o para verificar horÃ¡rio comercial
export function isBusinessHours(): boolean {
    const now = new Date()
    const hour = now.getHours()
    const day = now.getDay() // 0 = domingo, 6 = sÃ¡bado

    // Verificar se Ã© fim de semana
    if (day === 0 || day === 6) return false

    // Verificar horÃ¡rio
    return hour >= whatsappConfig.businessHours.start && hour < whatsappConfig.businessHours.end
}

// FunÃ§Ã£o para obter status de atendimento
export function getAttendanceStatus(): {
    isOpen: boolean
    message: string
    nextOpenTime?: string
} {
    const isOpen = isBusinessHours()

    if (isOpen) {
        return {
            isOpen: true,
            message: 'Online agora â€¢ Resposta rÃ¡pida'
        }
    }

    const now = new Date()
    const day = now.getDay()
    const hour = now.getHours()

    if (day === 0 || day === 6) {
        return {
            isOpen: false,
            message: 'Offline â€¢ Responderemos na segunda-feira',
            nextOpenTime: 'Segunda-feira Ã s 8h'
        }
    }

    if (hour < whatsappConfig.businessHours.start) {
        return {
            isOpen: false,
            message: 'Offline â€¢ Responderemos Ã s 8h',
            nextOpenTime: 'Hoje Ã s 8h'
        }
    }

    return {
        isOpen: false,
        message: 'Offline â€¢ Responderemos amanhÃ£',
        nextOpenTime: 'AmanhÃ£ Ã s 8h'
    }
}

