    @apply flex items-center gap-2 p-3 rounded-lg bg-laas-gray-50 border border-laas-gray-200;
  }

  .trust-badge-icon {
    @apply w-5 h-5 text-laas-success;
  }

  .trust-badge-text {
    @apply text-sm font-medium text-laas-gray-700;
  }

  /* Form Styles */
  .laas-input {
    @apply w-full rounded-lg border border-laas-gray-300 bg-white px-4 py-2.5 text-sm text-laas-gray-900 placeholder:text-laas-gray-400 focus:border-laas-blue focus:ring-2 focus:ring-laas-blue/20 focus:outline-none transition-colors;
  }

  .laas-label {
    @apply block text-sm font-medium text-laas-gray-700 mb-1.5;
  }

  /* Button Variants */
  .btn-primary {
    @apply bg-laas-blue hover:bg-laas-blue-dark text-white font-semibold px-6 py-3 rounded-lg transition-all duration-200 shadow-laas-md hover:shadow-laas-lg active:scale-[0.98];
  }

  .btn-secondary {
    @apply bg-white hover:bg-laas-gray-50 text-laas-blue border-2 border-laas-blue font-semibold px-6 py-3 rounded-lg transition-all duration-200;
  }

  .btn-whatsapp {
    @apply bg-laas-whatsapp hover:bg-[#20ba5a] text-white font-semibold px-6 py-3 rounded-lg transition-all duration-200 shadow-laas-md hover:shadow-laas-lg flex items-center gap-2;
  }

  .btn-ghost {
    @apply bg-transparent hover:bg-laas-gray-100 text-laas-gray-700 font-medium px-4 py-2 rounded-lg transition-colors;
  }

  /* Hero Section */
  .hero-title {
    @apply text-display-lg md:text-display-xl lg:text-display-2xl text-laas-blue leading-tight;
  }

  .hero-subtitle {
    @apply text-lg md:text-xl text-laas-gray-600 max-w-2xl;
  }

  /* Pricing Card */
  .pricing-card {
    @apply relative bg-white rounded-2xl border-2 border-laas-gray-200 p-8 hover:border-laas-blue hover:shadow-laas-xl transition-all duration-300;
  }

  .pricing-card-featured {
    @apply border-laas-blue shadow-laas-lg;
  }

  .pricing-card-badge {
    @apply absolute -top-3 left-1/2 -translate-x-1/2 bg-laas-blue text-white text-xs font-bold px-4 py-1 rounded-full;
  }

  /* FAQ Item */
  .faq-item {
    @apply border-b border-laas-gray-200 last:border-0;
  }

  .faq-trigger {
    @apply flex w-full items-center justify-between py-4 text-left text-base font-medium text-laas-gray-900 hover:text-laas-blue transition-colors;
  }

  .faq-content {
    @apply pb-4 text-sm text-laas-gray-600;
  }
}

@layer utilities {
  /* Gradient backgrounds */
  .gradient-primary {
    @apply bg-gradient-to-br from-laas-blue to-laas-blue-dark;
  }

  .gradient-light {
    @apply bg-gradient-to-br from-laas-gray-50 to-white;
  }

  /* Text gradients */
  .text-gradient-primary {
    @apply bg-gradient-to-r from-laas-blue to-laas-blue-medium bg-clip-text text-transparent;
  }

  /* Scrollbar */
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  /* Focus visible */
  .focus-ring {
    @apply focus:outline-none focus:ring-2 focus:ring-laas-blue focus:ring-offset-2;
  }
}

