'use client'

import { useState } from 'react'
import { useRouter, useSearchParams } from 'next/navigation'
import { useForm } from 'react-hook-form'
import { zodResolver } from '@hookform/resolvers/zod'
import { z } from 'zod'
import { Button } from '@/components/ui/Button'
import { Input } from '@/components/ui/Input'
import { Label } from '@/components/ui/Label'
import { Select } from '@/components/ui/Select'
import { Checkbox } from '@/components/ui/Checkbox'
import {
    leadFormSchema,
    personalInfoSchema,
    prescriptionSchema,
    preferencesSchema,
    validateCPF,
    formatCPF,
    formatPhone,
    formatCEP
} from '@/lib/validations'
import { pricingPlans } from '@/data/pricing-plans'

// Schema completo para agendamento
const schedulingFormSchema = z.object({
    // Dados de lead (podem vir preenchidos)
    leadInfo: leadFormSchema,

    // Dados pessoais completos
    personalInfo: personalInfoSchema,

    // Dados de prescriÃ§Ã£o
    prescription: prescriptionSchema,

    // PreferÃªncias
    preferences: preferencesSchema,

    // Plano selecionado
    selectedPlan: z.enum(['basic', 'premium', 'vip']),

    // Dados especÃ­ficos do agendamento
    scheduling: z.object({
        preferredDate: z.string().min(1, 'Data preferida Ã© obrigatÃ³ria'),
        preferredTime: z.enum(['morning', 'afternoon', 'evening']),
        consultationType: z.enum(['initial', 'followup', 'emergency']),
        additionalNotes: z.string().max(500, 'ObservaÃ§Ãµes devem ter no mÃ¡ximo 500 caracteres').optional()
    })
})

type SchedulingFormData = z.infer<typeof schedulingFormSchema>

export default function AgendarConsultaPage() {
    const router = useRouter()
    const searchParams = useSearchParams()
    const [currentStep, setCurrentStep] = useState(1)
    const [isSubmitting, setIsSubmitting] = useState(false)

    // PrÃ©-preencher dados se vieram da URL
    const prefilledData = {
        nome: searchParams.get('nome') || '',
        whatsapp: searchParams.get('whatsapp') || '',
        email: searchParams.get('email') || '',
        selectedPlan: (searchParams.get('plan') as 'basic' | 'premium' | 'vip') || 'premium'
    }

    const {
        register,
        handleSubmit,
        watch,
        setValue,
        formState: { errors },
        trigger
    } = useForm<SchedulingFormData>({
        resolver: zodResolver(schedulingFormSchema),
        defaultValues: {
            leadInfo: {
                nome: prefilledData.nome,
                whatsapp: prefilledData.whatsapp,
                email: prefilledData.email,
                lgpdConsent: false
            },
            selectedPlan: prefilledData.selectedPlan,
            prescription: {
                hasValidPrescription: false,
                needsConsultation: true,
                rightEye: { sphere: 0, cylinder: 0, axis: 0 },
                leftEye: { sphere: 0, cylinder: 0, axis: 0 },
                prescriptionDate: '',
                doctorName: ''
            },
            preferences: {
                lensType: 'monthly',
                deliveryFrequency: 'monthly',
                addOns: []
            },
            scheduling: {
                preferredDate: '',
                preferredTime: 'morning',
                consultationType: 'initial'
            }
        }
    })

    const selectedPlan = watch('selectedPlan')
    const hasValidPrescription = watch('prescription.hasValidPrescription')
    const needsConsultation = watch('prescription.needsConsultation')

    const steps = [
        { id: 1, title: 'Dados Pessoais', description: 'InformaÃ§Ãµes bÃ¡sicas e contato' },
        { id: 2, title: 'EndereÃ§o', description: 'Dados para entrega' },
        { id: 3, title: 'PrescriÃ§Ã£o', description: 'Dados oftalmolÃ³gicos' },
        { id: 4, title: 'PreferÃªncias', description: 'Tipo de lente e entrega' },
        { id: 5, title: 'Agendamento', description: 'Data e horÃ¡rio da consulta' }
    ]

    const handleNextStep = async () => {
        let fieldsToValidate: (keyof SchedulingFormData)[] = []

        switch (currentStep) {
            case 1:
                fieldsToValidate = ['leadInfo']
                break
            case 2:
                fieldsToValidate = ['personalInfo']
                break
            case 3:
                fieldsToValidate = ['prescription']
                break
            case 4:
                fieldsToValidate = ['preferences']
                break
        }

        const isValid = await trigger(fieldsToValidate)
        if (isValid && currentStep < 5) {
            setCurrentStep(currentStep + 1)
        }
    }

    const handlePrevStep = () => {
        if (currentStep > 1) {
            setCurrentStep(currentStep - 1)
        }
    }

    const onSubmit = async (data: SchedulingFormData) => {
        setIsSubmitting(true)

        try {
            // Aqui seria a integraÃ§Ã£o com o sistema de agendamento
            const response = await fetch('/api/schedule-consultation', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            })

            if (response.ok) {
                const result = await response.json()
                // Redirecionar para pÃ¡gina de confirmaÃ§Ã£o
                router.push(`/agendamento-confirmado?id=${result.schedulingId}`)
            } else {
                throw new Error('Erro ao agendar consulta')
            }
        } catch (error) {
            console.error('Erro no agendamento:', error)
            alert('Erro ao agendar consulta. Tente novamente.')
        } finally {
            setIsSubmitting(false)
        }
    }

    const selectedPlanData = pricingPlans.find(plan => plan.id === selectedPlan)

    return (
        <div className="min-h-screen bg-gray-50 py-8">
            <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
                {/* Header */}
                <div className="text-center mb-8">
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">
                        Agendar Consulta
                    </h1>
                    <p className="text-lg text-gray-600">
                        Complete seus dados para agendar sua consulta com Dr. Philipe Saraiva Cruz
                    </p>
                </div>

                {/* Progress Steps */}
                <div className="mb-8">
                    <div className="flex items-center justify-between">
                        {steps.map((step, index) => (
                            <div key={step.id} className="flex items-center">
                                <div className={`
                  flex items-center justify-center w-10 h-10 rounded-full border-2 
                  ${currentStep >= step.id
                                        ? 'bg-blue-600 border-blue-600 text-white'
                                        : 'bg-white border-gray-300 text-gray-500'
                                    }
                `}>
                                    {step.id}
                                </div>
                                <div className="ml-3 hidden sm:block">
                                    <p className={`text-sm font-medium ${currentStep >= step.id ? 'text-blue-600' : 'text-gray-500'
                                        }`}>
                                        {step.title}
                                    </p>
                                    <p className="text-xs text-gray-500">{step.description}</p>
                                </div>
                                {index < steps.length - 1 && (
                                    <div className={`
                    hidden sm:block w-16 h-0.5 ml-4
                    ${currentStep > step.id ? 'bg-blue-600' : 'bg-gray-300'}
                  `} />
                                )}
                            </div>
                        ))}
                    </div>
                </div>

                {/* Selected Plan Info */}
                {selectedPlanData && (
                    <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
                        <div className="flex items-center justify-between">
                            <div>
                                <h3 className="font-semibold text-blue-900">{selectedPlanData.name}</h3>
                                <p className="text-blue-700">
                                    R$ {selectedPlanData.priceMonthly.toFixed(2).replace('.', ',')} /mÃªs
                                </p>
                            </div>
                            <Button
                                type="button"
                                variant="outline"
                                size="sm"
                                onClick={() => router.push('/#planos-precos')}
                            >
                                Alterar Plano
                            </Button>
                        </div>
                    </div>
                )}

                {/* Form */}
                <form onSubmit={handleSubmit(onSubmit)} className="bg-white rounded-lg shadow-sm p-6">
                    {/* Step 1: Dados Pessoais */}
                    {currentStep === 1 && (
                        <div className="space-y-6">
                            <h2 className="text-xl font-semibold text-gray-900 mb-4">
                                Dados Pessoais
                            </h2>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <div>
                                    <Label htmlFor="nome">Nome Completo *</Label>
                                    <Input
                                        id="nome"
                                        {...register('leadInfo.nome')}
                                        placeholder="Seu nome completo"
                                        error={errors.leadInfo?.nome?.message}
                                    />
                                </div>

                                <div>
                                    <Label htmlFor="whatsapp">WhatsApp *</Label>
                                    <Input
                                        id="whatsapp"
                                        {...register('leadInfo.whatsapp')}
                                        placeholder="(11) 99999-9999"
                                        onChange={(e) => {
                                            const formatted = formatPhone(e.target.value)
                                            setValue('leadInfo.whatsapp', formatted)
                                        }}
                                        error={errors.leadInfo?.whatsapp?.message}
                                    />
                                </div>

                                <div>
                                    <Label htmlFor="email">Email *</Label>
                                    <Input
                                        id="email"
                                        type="email"
                                        {...register('leadInfo.email')}
                                        placeholder="seu@email.com"
                                        error={errors.leadInfo?.email?.message}
                                    />
                                </div>

