import { trackEvent } from '@/lib/analytics'
import { trackLeadCapture } from '@/lib/conversion-tracking'
import { MarketingOptIn } from '@/components/privacy/MarketingOptIn'
import { setMarketingConsent } from '@/lib/privacy'
import { Calculator, Phone, MessageCircle, Shield, CheckCircle } from 'lucide-react'

interface LeadCaptureFormProps {
    variant?: 'hero' | 'calculator' | 'inline'
    onSubmit?: (data: LeadFormData) => void
    className?: string
}

export function LeadCaptureForm({
    variant = 'hero',
    onSubmit,
    className = ''
}: LeadCaptureFormProps) {
    const [isSubmitting, setIsSubmitting] = useState(false)
    const [isSubmitted, setIsSubmitted] = useState(false)
    const [marketingOptIn, setMarketingOptIn] = useState(false)

    const {
        register,
        handleSubmit,
        formState: { errors },
        watch,
        setValue
    } = useForm<LeadFormData>({
        resolver: zodResolver(leadFormSchema),
        defaultValues: {
            nome: '',
            whatsapp: '',
            email: '',
            lgpdConsent: false
        }
    })

    const watchedPhone = watch('whatsapp')

    // Formatar telefone em tempo real
    const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const formatted = formatPhone(e.target.value)
        setValue('whatsapp', formatted)
    }

    const handleFormSubmit = async (data: LeadFormData) => {
        setIsSubmitting(true)

        try {
            // Simular envio (aqui vocÃª integraria com sua API)
            await new Promise(resolve => setTimeout(resolve, 1000))

            // Save marketing consent
            if (marketingOptIn) {
                setMarketingConsent(true, {
                    email: true,
                    whatsapp: true,
                    sms: false
                })
            }

            // Callback personalizado se fornecido
            if (onSubmit) {
                onSubmit(data)
            } else {
                // Comportamento padrÃ£o: redirecionar para WhatsApp
                openWhatsAppWithContext('hero', {
                    page: 'landing-page',
                    section: 'lead-form',
                    userInfo: {
                        nome: data.nome,
                        email: data.email,
                        whatsapp: data.whatsapp
                    }
                })
            }

            setIsSubmitted(true)

            // Analytics tracking
            trackLeadCapture({
                nome: data.nome,
                email: data.email,
                whatsapp: data.whatsapp,
                source: variant || 'hero_form'
            })

        } catch (error) {
            console.error('Erro ao enviar formulÃ¡rio:', error)
        } finally {
            setIsSubmitting(false)
        }
    }

    const handleCalculateEconomy = () => {
        const formData = watch()
        if (formData.nome && formData.whatsapp && formData.email && formData.lgpdConsent) {
            // Se o formulÃ¡rio estÃ¡ preenchido, prosseguir para calculadora
            onSubmit?.(formData as LeadFormData)
        } else {
            // Focar no primeiro campo vazio
            const firstEmptyField = document.querySelector('input:invalid') as HTMLInputElement
            firstEmptyField?.focus()
        }
    }

    if (isSubmitted) {
        return (
            <div className={`bg-green-50 border border-green-200 rounded-lg p-6 text-center ${className}`}>
                <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                    <CheckCircle className="w-8 h-8 text-green-600" />
                </div>
                <h3 className="text-lg font-semibold text-green-800 mb-2">
                    Obrigado pelo interesse!
                </h3>
                <p className="text-green-700 mb-4">
                    Em breve entraremos em contato via WhatsApp para agendar sua consulta.
                </p>
                <Button
                    onClick={() => setIsSubmitted(false)}
                    variant="outline"
                    size="sm"
                >
                    Enviar outro formulÃ¡rio
                </Button>
            </div>
        )
    }

    if (variant === 'inline') {
        return (
            <form onSubmit={handleSubmit(handleFormSubmit)} className={`space-y-4 ${className}`}>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <Input
                        {...register('nome')}
                        placeholder="Seu nome"
                        error={errors.nome?.message}
                    />
                    <Input
                        {...register('whatsapp')}
                        placeholder="WhatsApp"
                        onChange={handlePhoneChange}
                        error={errors.whatsapp?.message}
                    />
                    <Input
                        {...register('email')}
                        type="email"
                        placeholder="Seu email"
                        error={errors.email?.message}
                    />
                </div>

                <div className="flex items-center justify-between">
                    <Checkbox
                        {...register('lgpdConsent')}
                        error={errors.lgpdConsent?.message}
                    >
                        <span className="text-xs text-gray-600">
                            Aceito receber contato e concordo com a{' '}
                            <a href="/politica-privacidade" className="text-primary-600 hover:underline">
                                polÃ­tica de privacidade
                            </a>
                        </span>
                    </Checkbox>

                    <Button
                        type="submit"
                        loading={isSubmitting}
                        disabled={!watch('lgpdConsent')}
                        className="flex items-center space-x-2"
                    >
                        <MessageCircle className="w-4 h-4" />
                        <span>Enviar</span>
                    </Button>
                </div>
            </form>
        )
    }

    return (
        <div className={`bg-white rounded-lg shadow-lg border border-gray-200 p-6 ${className}`}>
            <div className="text-center mb-6">
                <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-3">
                    <Calculator className="w-6 h-6 text-primary-600" />
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">
                    Calcule sua Economia
                </h3>
                <p className="text-sm text-gray-600">
                    Descubra quanto vocÃª pode economizar com nossa assinatura
                </p>
            </div>

            <form onSubmit={handleSubmit(handleFormSubmit)} className="space-y-4">
                <Input
                    {...register('nome')}
                    id="lead-nome"
                    label="Nome completo"
                    placeholder="Digite seu nome"
                    error={errors.nome?.message}
                    required
                />

                <Input
                    {...register('whatsapp')}
                    id="lead-whatsapp"
                    label="WhatsApp"
                    placeholder="(11) 99999-9999"
                    onChange={handlePhoneChange}
                    error={errors.whatsapp?.message}
                    helperText="Usaremos para enviar o resultado da calculadora"
                    required
                />

                <Input
                    {...register('email')}
                    id="lead-email"
                    type="email"
                    label="Email"
                    placeholder="seu@email.com"
                    error={errors.email?.message}
                    required
                />

                <Checkbox
                    {...register('lgpdConsent')}
                    id="lead-lgpd-consent"
                    error={errors.lgpdConsent?.message}
                    required
                >
                    <span className="text-sm text-gray-800 leading-relaxed">
                        Aceito receber contato sobre o serviÃ§o de assinatura de lentes de contato da SV Lentes e concordo com a{' '}
                        <a
                            href="/politica-privacidade"
                            className="text-primary-700 hover:text-primary-800 underline font-medium"
                            target="_blank"
                            rel="noopener noreferrer"
                        >
                            polÃ­tica de privacidade
                        </a>
                        .
                    </span>
                </Checkbox>

                <MarketingOptIn
                    onOptInChange={setMarketingOptIn}
                    className="pt-2 border-t border-gray-100"
                />

                <div className="space-y-3">
                    <Button
                        onClick={handleCalculateEconomy}
                        type="button"
                        className="w-full flex items-center justify-center space-x-2 font-semibold text-base"
                        disabled={!watch('lgpdConsent')}
                        aria-label="Calcular economia com assinatura de lentes"
                    >
                        <Calculator className="w-5 h-5" aria-hidden="true" />
                        <span>Calcular Economia</span>
                    </Button>

                    <div className="relative">
                        <div className="absolute inset-0 flex items-center">
                            <div className="w-full border-t border-gray-200"></div>
                        </div>
                        <div className="relative flex justify-center text-sm">
                            <span className="px-2 bg-white text-gray-500">ou</span>
                        </div>
                    </div>

                    <Button
                        type="submit"
                        variant="outline"
                        loading={isSubmitting}
                        disabled={!watch('lgpdConsent')}
                        className="w-full flex items-center justify-center space-x-2 font-semibold text-base"
                        aria-label="Agendar consulta oftalmolÃ³gica diretamente"
                    >
                        <Phone className="w-5 h-5" aria-hidden="true" />
                        <span>Agendar Consulta Direta</span>
