'use client';

import { useState } from 'react';
import { CalculatorInput, CalculatorResult } from '@/types';
import { usagePatterns, lensTypes } from '@/data/calculator-data';
import { calculateEconomy } from '@/lib/calculator';

interface CalculatorFormProps {
  onCalculate: (result: CalculatorResult) => void;
  initialData?: Partial<CalculatorInput>;
}

export function CalculatorForm({ onCalculate, initialData }: CalculatorFormProps) {
  const [input, setInput] = useState<CalculatorInput>({
    lensType: initialData?.lensType || 'daily',
    usagePattern: initialData?.usagePattern || 'regular',
    currentMonthlySpend: initialData?.currentMonthlySpend
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const result = calculateEconomy(input);
      onCalculate(result);
    } catch (error) {
      console.error('Erro no cÃ¡lculo:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Tipo de Lente */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-3">
          Que tipo de lente vocÃª usa?
        </label>
        <div className="grid grid-cols-1 gap-3">
          {lensTypes.map((lens) => (
            <label
              key={lens.id}
              className={`relative flex cursor-pointer rounded-lg border p-4 focus:outline-none ${
                input.lensType === lens.id
                  ? 'border-blue-600 bg-blue-50'
                  : 'border-gray-300 bg-white hover:bg-gray-50'
              }`}
            >
              <input
                type="radio"
                name="lensType"
                value={lens.id}
                checked={input.lensType === lens.id}
                onChange={(e) => setInput({ ...input, lensType: e.target.value })}
                className="sr-only"
              />
              <div className="flex w-full items-center justify-between">
                <div>
                  <div className="text-sm font-medium text-gray-900">
                    {lens.name}
                  </div>
                </div>
                <div className={`h-4 w-4 rounded-full border-2 ${
                  input.lensType === lens.id
                    ? 'border-blue-600 bg-blue-600'
                    : 'border-gray-300'
                }`}>
                  {input.lensType === lens.id && (
                    <div className="h-full w-full rounded-full bg-white scale-50" />
                  )}
                </div>
              </div>
            </label>
          ))}
        </div>
      </div>

      {/* PadrÃ£o de Uso */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-3">
          Com que frequÃªncia vocÃª usa lentes?
        </label>
        <div className="grid grid-cols-1 gap-3">
          {usagePatterns.map((pattern) => (
            <label
              key={pattern.id}
              className={`relative flex cursor-pointer rounded-lg border p-4 focus:outline-none ${
                input.usagePattern === pattern.id
                  ? 'border-blue-600 bg-blue-50'
                  : 'border-gray-300 bg-white hover:bg-gray-50'
              }`}
            >
              <input
                type="radio"
                name="usagePattern"
                value={pattern.id}
                checked={input.usagePattern === pattern.id}
                onChange={(e) => setInput({ ...input, usagePattern: e.target.value })}
                className="sr-only"
              />
              <div className="flex w-full items-center justify-between">
                <div>
                  <div className="text-sm font-medium text-gray-900">
                    {pattern.name}
                  </div>
                  <div className="text-sm text-gray-500">
                    {pattern.description}
                  </div>
                </div>
                <div className={`h-4 w-4 rounded-full border-2 ${
                  input.usagePattern === pattern.id
                    ? 'border-blue-600 bg-blue-600'
                    : 'border-gray-300'
                }`}>
                  {input.usagePattern === pattern.id && (
                    <div className="h-full w-full rounded-full bg-white scale-50" />
                  )}
                </div>
              </div>
            </label>
          ))}
        </div>
      </div>

      {/* BotÃ£o de Calcular */}
      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-blue-700 transition-colors"
      >
        Calcular Economia
      </button>
    </form>
  );
}

src/components/forms/lead-calculator-for.content:
'use client';

import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { leadFormSchema, type LeadFormData } from '@/lib/validations';
import { CalculatorResult } from '@/types';
import { CalculatorForm } from './calculator-form';
import { CalculatorResults } from '../sections/calculator-results';

interface LeadCalculatorFormProps {
  onLeadSubmit?: (data: LeadFormData & { calculatorResult?: CalculatorResult }) => void;
}

export function LeadCalculatorForm({ onLeadSubmit }: LeadCalculatorFormProps) {
  const [step, setStep] = useState<'calculator' | 'results' | 'lead'>('calculator');
  const [calculatorResult, setCalculatorResult] = useState<CalculatorResult | null>(null);

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting }
  } = useForm<LeadFormData>({
    resolver: zodResolver(leadFormSchema)
  });

  const handleCalculate = (result: CalculatorResult) => {
    setCalculatorResult(result);
    setStep('results');
  };

  const handleShowLeadForm = () => {
    setStep('lead');
  };

  const handleLeadSubmit = async (data: LeadFormData) => {
    if (onLeadSubmit) {
      await onLeadSubmit({ ...data, calculatorResult: calculatorResult || undefined });
    }
  };

  const handleReset = () => {
    setStep('calculator');
    setCalculatorResult(null);
  };

  return (
    <div className="max-w-md mx-auto">
      {step === 'calculator' && (
        <div>
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            Calcule sua economia
          </h3>
          <CalculatorForm onCalculate={handleCalculate} />
        </div>
      )}

      {step === 'results' && calculatorResult && (
        <div>
          <CalculatorResults result={calculatorResult} onReset={handleReset} />
          <div className="mt-6">
            <button
              onClick={handleShowLeadForm}
              className="w-full bg-green-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-green-700 transition-colors"
            >
              Quero Agendar Consulta
            </button>
          </div>
        </div>
      )}

      {step === 'lead' && (
        <div>
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            Agende sua consulta
          </h3>

          {calculatorResult && (
            <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
              <div className="text-center">
                <div className="text-sm text-green-700 mb-1">Sua economia mensal:</div>
                <div className="text-xl font-bold text-green-800">
                  R$ {calculatorResult.monthlySavings.toFixed(2)}
                </div>
              </div>
            </div>
          )}

          <form onSubmit={handleSubmit(handleLeadSubmit)} className="space-y-4">
            <div>
              <label htmlFor="nome" className="block text-sm font-medium text-gray-700 mb-1">
                Nome completo
              </label>
              <input
                {...register('nome')}
                id="calculator-nome"
                type="text"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="Seu nome completo"
              />
              {errors.nome && (
                <p className="mt-1 text-sm text-red-600">{errors.nome.message}</p>
              )}
            </div>

            <div>
              <label htmlFor="whatsapp" className="block text-sm font-medium text-gray-700 mb-1">
                WhatsApp
              </label>
              <input
                {...register('whatsapp')}
                id="calculator-whatsapp"
                type="tel"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="(11) 99999-9999"
              />
              {errors.whatsapp && (
                <p className="mt-1 text-sm text-red-600">{errors.whatsapp.message}</p>
              )}
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                E-mail
              </label>
              <input
                {...register('email')}
                id="calculator-email"
                type="email"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="seu@email.com"
              />
              {errors.email && (
                <p className="mt-1 text-sm text-red-600">{errors.email.message}</p>
              )}
            </div>

            <div className="flex items-start">
              <input
                {...register('lgpdConsent')}
                id="calculator-lgpd-consent"
                type="checkbox"
                className="mt-1 h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
              />
              <label htmlFor="lgpdConsent" className="ml-2 text-sm text-gray-600">
                Concordo em receber informaÃ§Ãµes sobre os serviÃ§os de assinatura de lentes de contato da SV Lentes e com o tratamento dos meus dados conforme a{' '}
                <a href="/politica-privacidade" className="text-blue-600 hover:underline">
                  PolÃ­tica de Privacidade
                </a>
              </label>
            </div>
            {errors.lgpdConsent && (
              <p className="text-sm text-red-600">{errors.lgpdConsent.message}</p>
            )}

            <button
              type="submit"
              disabled={isSubmitting}
              className="w-full bg-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              {isSubmitting ? 'Enviando...' : 'Agendar Consulta'}
            </button>
          </form>

          <button
            onClick={handleReset}
            className="w-full mt-3 text-sm text-gray-600 hover:text-gray-800"
          >
            â† Voltar para calculadora
          </button>
        </div>
      )}
    </div>
  );
}

src/components/how-it-works/process-time.content:
"use client"

import { motion } from 'framer-motion'
import { cn } from '@/lib/utils'

interface TimelineStep {
  label: string
  index: number
  isActive: boolean
}

interface ProcessTimelineProps {
  steps: string[]
  activeStep?: number
}

export function ProcessTimeline({ steps, activeStep = 0 }: ProcessTimelineProps) {
  return (
    <div className="relative py-8">
      {/* Timeline container */}
      <div className="flex items-center justify-between relative">
        {/* Background line */}
        <div className="absolute top-1/2 left-0 right-0 h-1 bg-gray-200 -translate-y-1/2 z-0" />

        {/* Progress line */}
        <motion.div
          initial={{ width: 0 }}
          whileInView={{ width: `${(activeStep / (steps.length - 1)) * 100}%` }}
          viewport={{ once: true }}
          transition={{ duration: 1, delay: 0.3 }}
          className="absolute top-1/2 left-0 h-1 bg-gradient-to-r from-primary-500 to-primary-600 -translate-y-1/2 z-10"
        />

        {/* Timeline steps */}
        {steps.map((step, index) => {
          const isActive = index <= activeStep

          return (
            <motion.div
              key={index}
              initial={{ scale: 0 }}
              whileInView={{ scale: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.3, delay: index * 0.1 }}
              className="relative z-20 flex flex-col items-center"
            >
              {/* Step circle */}
              <div
                className={cn(
                  "w-10 h-10 rounded-full border-4 flex items-center justify-center transition-all duration-300",
                  isActive
                    ? "bg-primary-500 border-primary-200 shadow-lg"
                    : "bg-white border-gray-300"
                )}
              >
                {isActive ? (
                  <svg
                    className="w-5 h-5 text-white"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={3}
                      d="M5 13l4 4L19 7"
                    />
                  </svg>
                ) : (
                  <span className="text-sm font-semibold text-gray-400">
                    {index + 1}
                  </span>
                )}
              </div>

              {/* Step label */}
              <span
                className={cn(
                  "absolute top-14 text-xs text-center max-w-[100px] leading-tight transition-all duration-300",
                  isActive ? "text-primary-700 font-semibold" : "text-gray-500"
                )}
              >
                {step}
              </span>
            </motion.div>
          )
        })}
      </div>
    </div>
  )
}

src/components/layout/Header.tsx.content:
'use client'

import { useState, useEffect } from 'react'
import { Button } from '@/components/ui/Button'
import { LogoHeader } from '@/components/ui/Logo'
import { scrollToSection, generateWhatsAppLink } from '@/lib/utils'
import { Menu, X, Phone } from 'lucide-react'

interface HeaderProps {
    className?: string
}

export function Header({ className }: HeaderProps) {
    const [isMenuOpen, setIsMenuOpen] = useState(false)
    const [isScrolled, setIsScrolled] = useState(false)

    // Detectar scroll para adicionar sombra no header
    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 10)
        }

        window.addEventListener('scroll', handleScroll)
        return () => window.removeEventListener('scroll', handleScroll)
    }, [])

    // Fechar menu mobile ao redimensionar
    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth >= 768) {
                setIsMenuOpen(false)
            }
        }

        window.addEventListener('resize', handleResize)
        return () => window.removeEventListener('resize', handleResize)
    }, [])

    const navigation = [
        { name: 'Planos', href: '#planos-precos' },
        { name: 'Como Funciona', href: '#como-funciona' },
        { name: 'FAQ', href: '#perguntas-frequentes' },
        { name: 'Contato', href: '#contato' },
    ]

    const handleNavClick = (href: string) => {
        const sectionId = href.replace('#', '')
        scrollToSection(sectionId)
        setIsMenuOpen(false)
    }

    const handleAgendarConsulta = () => {
        const whatsappMessage = `OlÃ¡! Gostaria de agendar uma consulta com o Dr. Philipe Saraiva Cruz para avaliar minha necessidade de lentes de contato.

Vim atravÃ©s do site SV Lentes e tenho interesse no serviÃ§o de assinatura com acompanhamento mÃ©dico.`

        const whatsappLink = generateWhatsAppLink(
            process.env.NEXT_PUBLIC_WHATSAPP_NUMBER || '5511947038078',
            whatsappMessage
        )

        window.open(whatsappLink, '_blank')
    }

    return (
        <header
            className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${isScrolled
                    ? 'bg-white/95 backdrop-blur-md shadow-lg border-b border-gray-200/50'
                    : 'bg-white shadow-sm'
                } ${className}`}
        >
            <div className="container-custom">
                <div className="flex items-center justify-between h-16 lg:h-20">
                    {/* Logo */}
                    <a
                        href="#hero"
                        onClick={(e) => {
                            e.preventDefault()
                            scrollToSection('hero')
