'use client'

import { useState } from 'react'
import { Eye, Info } from 'lucide-react'
import { Button } from '@/components/ui/Button'
import { Input } from '@/components/ui/Input'

interface LensData {
    type: 'daily' | 'weekly' | 'monthly'
    brand: string
    rightEye: {
        sphere: string
        cylinder: string
        axis: string
    }
    leftEye: {
        sphere: string
        cylinder: string
        axis: string
    }
}

interface LensSelectorProps {
    onContinue: (data: LensData) => void
    onBack: () => void
}

const lensTypes = [
    { id: 'daily', name: 'DiÃ¡rias', description: 'Uso Ãºnico, mÃ¡ximo conforto' },
    { id: 'weekly', name: 'Semanais', description: 'Troca semanal' },
    { id: 'monthly', name: 'Mensais', description: 'Troca mensal, econÃ´micas' }
]

const popularBrands = [
    'Acuvue',
    'Biofinity',
    'Air Optix',
    'Bausch + Lomb',
    'CooperVision',
    'Outra marca'
]

export function LensSelector({ onContinue, onBack }: LensSelectorProps) {
    const [lensData, setLensData] = useState<LensData>({
        type: 'monthly',
        brand: '',
        rightEye: { sphere: '', cylinder: '', axis: '' },
        leftEye: { sphere: '', cylinder: '', axis: '' }
    })

    const [sameForBothEyes, setSameForBothEyes] = useState(false)

    const handleTypeSelect = (type: 'daily' | 'weekly' | 'monthly') => {
        setLensData(prev => ({ ...prev, type }))
    }

    const handleRightEyeChange = (field: keyof LensData['rightEye'], value: string) => {
        setLensData(prev => ({
            ...prev,
            rightEye: { ...prev.rightEye, [field]: value },
            ...(sameForBothEyes && { leftEye: { ...prev.rightEye, [field]: value } })
        }))
    }

    const handleLeftEyeChange = (field: keyof LensData['leftEye'], value: string) => {
        setLensData(prev => ({
            ...prev,
            leftEye: { ...prev.leftEye, [field]: value }
        }))
    }

    const isValid = () => {
        return (
            lensData.brand &&
            lensData.rightEye.sphere &&
            lensData.leftEye.sphere
        )
    }

    return (
        <div className="space-y-8">
            {/* Lens Type Selection */}
            <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                    Tipo de Lente
                </h3>
                <div className="grid md:grid-cols-3 gap-4">
                    {lensTypes.map((type) => (
                        <button
                            key={type.id}
                            onClick={() => handleTypeSelect(type.id as any)}
                            className={`p-4 rounded-lg border-2 text-left transition-all ${lensData.type === type.id
                                    ? 'border-primary-600 bg-primary-50'
                                    : 'border-gray-200 hover:border-gray-300'
                                }`}
                        >
                            <div className="font-semibold text-gray-900 mb-1">
                                {type.name}
                            </div>
                            <div className="text-sm text-gray-600">
                                {type.description}
                            </div>
                        </button>
                    ))}
                </div>
            </div>

            {/* Brand Selection */}
            <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                    Marca Atual (opcional)
                </h3>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                    {popularBrands.map((brand) => (
                        <button
                            key={brand}
                            onClick={() => setLensData(prev => ({ ...prev, brand }))}
                            className={`px-4 py-3 rounded-lg border-2 text-sm font-medium transition-all ${lensData.brand === brand
                                    ? 'border-primary-600 bg-primary-50 text-primary-700'
                                    : 'border-gray-200 text-gray-700 hover:border-gray-300'
                                }`}
                        >
                            {brand}
                        </button>
                    ))}
                </div>
            </div>

            {/* Prescription */}
            <div>
                <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-semibold text-gray-900">
                        Grau das Lentes
                    </h3>
                    <button
                        onClick={() => setSameForBothEyes(!sameForBothEyes)}
                        className="text-sm text-primary-600 hover:text-primary-700 font-medium"
                    >
                        {sameForBothEyes ? 'Graus diferentes' : 'Mesmo grau para ambos'}
                    </button>
                </div>

                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6 flex items-start space-x-3">
                    <Info className="w-5 h-5 text-blue-600 flex-shrink-0 mt-0.5" />
                    <div className="text-sm text-blue-900">
                        <p className="font-medium mb-1">Encontre seu grau na receita</p>
                        <p className="text-blue-700">
                            EsfÃ©rico (ESF), CilÃ­ndrico (CIL) e Eixo sÃ£o os valores principais.
                            Se nÃ£o tiver certeza, podemos ajudar na consulta.
                        </p>
                    </div>
                </div>

                {/* Right Eye */}
                <div className="mb-6">
                    <div className="flex items-center space-x-2 mb-3">
                        <Eye className="w-5 h-5 text-gray-600" />
                        <h4 className="font-semibold text-gray-900">Olho Direito (OD)</h4>
                    </div>
                    <div className="grid grid-cols-3 gap-4">
                        <Input
                            label="EsfÃ©rico"
                            placeholder="-2.00"
                            value={lensData.rightEye.sphere}
                            onChange={(e) => handleRightEyeChange('sphere', e.target.value)}
                        />
                        <Input
                            label="CilÃ­ndrico"
                            placeholder="-0.75"
                            value={lensData.rightEye.cylinder}
                            onChange={(e) => handleRightEyeChange('cylinder', e.target.value)}
                        />
                        <Input
                            label="Eixo"
                            placeholder="180"
                            value={lensData.rightEye.axis}
                            onChange={(e) => handleRightEyeChange('axis', e.target.value)}
                        />
                    </div>
                </div>

                {/* Left Eye */}
                {!sameForBothEyes && (
                    <div>
                        <div className="flex items-center space-x-2 mb-3">
                            <Eye className="w-5 h-5 text-gray-600" />
                            <h4 className="font-semibold text-gray-900">Olho Esquerdo (OE)</h4>
                        </div>
                        <div className="grid grid-cols-3 gap-4">
                            <Input
                                label="EsfÃ©rico"
                                placeholder="-2.00"
                                value={lensData.leftEye.sphere}
                                onChange={(e) => handleLeftEyeChange('sphere', e.target.value)}
                            />
                            <Input
                                label="CilÃ­ndrico"
                                placeholder="-0.75"
                                value={lensData.leftEye.cylinder}
                                onChange={(e) => handleLeftEyeChange('cylinder', e.target.value)}
                            />
                            <Input
                                label="Eixo"
                                placeholder="180"
                                value={lensData.leftEye.axis}
                                onChange={(e) => handleLeftEyeChange('axis', e.target.value)}
                            />
                        </div>
                    </div>
                )}
            </div>

            {/* Actions */}
            <div className="flex space-x-4">
                <Button
                    variant="outline"
                    onClick={onBack}
                    className="flex-1"
                >
                    Voltar
                </Button>
                <Button
                    onClick={() => onContinue(lensData)}
                    disabled={!isValid()}
                    className="flex-1"
                >
                    Continuar
                </Button>
            </div>
        </div>
    )
}

src/components/subscription/PlanSelector.content:
'use client'

import { useState } from 'react'
import { Check, Star } from 'lucide-react'
import { Button } from '@/components/ui/Button'
import { pricingPlans } from '@/data/pricing-plans'

interface PlanSelectorProps {
    onSelectPlan: (planId: string, billingCycle: 'monthly' | 'annual') => void
}

export function PlanSelector({ onSelectPlan }: PlanSelectorProps) {
    const [billingCycle, setBillingCycle] = useState<'monthly' | 'annual'>('monthly')
    const [selectedPlan, setSelectedPlan] = useState<string | null>(null)

    const handleSelectPlan = (planId: string) => {
        setSelectedPlan(planId)
        onSelectPlan(planId, billingCycle)
    }

    return (
        <div className="space-y-6">
            {/* Billing Cycle Toggle */}
            <div className="flex justify-center">
                <div className="inline-flex items-center bg-gray-100 rounded-lg p-1">
                    <button
                        onClick={() => setBillingCycle('monthly')}
                        className={`px-6 py-2 rounded-md text-sm font-medium transition-all ${billingCycle === 'monthly'
                                ? 'bg-white text-gray-900 shadow-sm'
                                : 'text-gray-600 hover:text-gray-900'
                            }`}
                    >
                        Mensal
                    </button>
                    <button
                        onClick={() => setBillingCycle('annual')}
                        className={`px-6 py-2 rounded-md text-sm font-medium transition-all ${billingCycle === 'annual'
                                ? 'bg-white text-gray-900 shadow-sm'
                                : 'text-gray-600 hover:text-gray-900'
                            }`}
                    >
                        Anual
                        <span className="ml-2 text-xs text-green-600 font-semibold">
                            Economize 1 mÃªs
                        </span>
                    </button>
                </div>
            </div>

            {/* Plans Grid */}
            <div className="grid md:grid-cols-3 gap-6">
                {pricingPlans.map((plan) => {
                    const price = billingCycle === 'monthly' ? plan.priceMonthly : plan.priceAnnual / 12
                    const isSelected = selectedPlan === plan.id

                    return (
                        <div
                            key={plan.id}
                            className={`relative bg-white rounded-xl border-2 transition-all ${isSelected
                                    ? 'border-primary-600 shadow-lg scale-105'
                                    : plan.recommended
                                        ? 'border-primary-300 shadow-md'
                                        : 'border-gray-200 hover:border-gray-300'
                                }`}
                        >
                            {/* Recommended Badge */}
                            {plan.recommended && (
                                <div className="absolute -top-4 left-1/2 -translate-x-1/2">
                                    <div className="bg-primary-600 text-white px-4 py-1 rounded-full text-xs font-semibold flex items-center space-x-1">
                                        <Star className="w-3 h-3 fill-current" />
                                        <span>Mais Popular</span>
                                    </div>
                                </div>
                            )}

                            <div className="p-6">
                                {/* Plan Header */}
                                <div className="text-center mb-6">
                                    <h3 className="text-xl font-bold text-gray-900 mb-2">
                                        {plan.name}
                                    </h3>
                                    <div className="flex items-baseline justify-center">
                                        <span className="text-4xl font-bold text-gray-900">
                                            R$ {price.toFixed(2)}
                                        </span>
                                        <span className="text-gray-600 ml-2">/mÃªs</span>
                                    </div>
                                    {billingCycle === 'annual' && (
                                        <p className="text-sm text-gray-500 mt-1">
                                            R$ {plan.priceAnnual.toFixed(2)} cobrado anualmente
                                        </p>
                                    )}
                                </div>

                                {/* Features List */}
                                <ul className="space-y-3 mb-6">
                                    {plan.features.map((feature, index) => (
                                        <li key={index} className="flex items-start space-x-3">
                                            <Check className="w-5 h-5 text-green-600 flex-shrink-0 mt-0.5" />
                                            <span className="text-sm text-gray-700">{feature}</span>
                                        </li>
                                    ))}
                                </ul>

                                {/* CTA Button */}
                                <Button
                                    onClick={() => handleSelectPlan(plan.id)}
                                    variant={isSelected ? 'default' : plan.recommended ? 'default' : 'outline'}
                                    className="w-full"
                                >
                                    {isSelected ? 'Selecionado' : plan.ctaText}
                                </Button>
                            </div>
                        </div>
                    )
                })}
            </div>
        </div>
    )
}

