    return (
      <div className={cn('relative flex-shrink-0', className)}>
        <Image
          src={config.src}
          alt="SV Lentes"
          width={config.height} // Square for icon
          height={config.height}
          priority={priority}
          className="object-contain"
          quality={95}
          sizes={`${config.height}px`}
        />
      </div>
    )
