// FunÃ§Ã£o para processar eventos de checkout completado
async function handleCheckoutCompleted(session: Stripe.Checkout.Session) {
    try {
        const customerEmail = session.customer_email
        const customerId = session.customer as string
        const subscriptionId = session.subscription as string
        const metadata = session.metadata || {}

        logWebhookEvent({
            eventType: 'checkout.session.completed',
            eventId: session.id,
            timestamp: new Date().toISOString(),
            customerId,
            subscriptionId,
            amount: session.amount_total ? session.amount_total / 100 : undefined,
            status: 'success',
            details: {
                customerEmail,
                planId: metadata.planId,
                billingInterval: metadata.billingInterval,
                planName: metadata.planName,
                source: metadata.source
            }
        })

        // LÃ³gica de negÃ³cio apÃ³s checkout completado:

        // 1. Registrar conversÃ£o para analytics
        if (metadata.planId && metadata.billingInterval) {
            console.log('CONVERSION_EVENT:', {
                event: 'subscription_purchased',
                planId: metadata.planId,
                billingInterval: metadata.billingInterval,
                customerId,
                value: session.amount_total ? session.amount_total / 100 : 0,
                currency: 'BRL'
            })
        }

        // 2. Preparar dados para prÃ³ximos passos
        const customerData = {
            stripeCustomerId: customerId,
            email: customerEmail,
            subscriptionId,
            planId: metadata.planId,
            planName: metadata.planName,
            billingInterval: metadata.billingInterval,
            checkoutCompletedAt: new Date().toISOString()
        }

        // 3. Aqui vocÃª integraria com:
        // - Banco de dados para salvar o cliente
